Ability.Lua:
if not PQI_MentallyDefensiveSettings_PauseRotation_enable then return false end

if PQI:IsHotkeys(PQI_MentallyDefensiveSettings_PauseRotation_key)
	and not GetCurrentKeyBoardFocus() then return true end

local PQ_VT = PQ_VT local PQ_PowerRound = PQ_PowerRound local mentallyPower = mentallyPower local TargetValidation = TargetValidation local isMindControledUnit = isMindControledUnit local tick = PQ_Round(3/(1+(UnitSpellHaste(&quot;player&quot;)/100))) local GCD = PQ_Round(1.5 / ((UnitSpellHaste(&quot;player&quot;) / 100) + 1),2) local max_timer = PQ_Round(PQ_PowerRound(15/(3/(1+(UnitSpellHaste(&quot;player&quot;)/100))))*(3/(1+(UnitSpellHaste(&quot;player&quot;)/100))),2)
if not PQI_MentallyOffensiveSettings_AutoBossDotting_enable then return false end if not PQR_SpellAvailable(PQ_VT) then return false end if PQR_IsMoving(0.1) then return false end
for i=1,4 do local bossUnit = &quot;boss&quot;..i local vampiricTouch = select(7,UnitDebuffID(bossUnit,PQ_VT,&quot;PLAYER&quot;))
-- Let&apos;s try and detect immunities if UnitBuffID(bossUnit,116994) or UnitBuffID(bossUnit,122540) or UnitBuffID(bossUnit,123250) or UnitBuffID(bossUnit,106062) or UnitBuffID(bossUnit,110945) then return false end
if TargetValidation(bossUnit,PQ_VT) then local id = UnitGUID(bossUnit)
if vampiricTouch then for i=1,#dotTracker do if dotTracker[i].guid == id and dotTracker[i].spellID == PQ_VT then if UnitBuffID(&quot;player&quot;,138963) then if dotTracker[i].crit == true then if vampiricTouch - GetTime() &lt; 10 then CastSpellByName(GetSpellInfo(PQ_VT),bossUnit) return true end end if dotTracker[i].crit == false then if doomTimer - GetTime() &lt; max_timer-1 then CastSpellByName(GetSpellInfo(PQ_VT),bossUnit) return true end end else if mentallyPower(34914) &gt; dotTracker[i].vtPower then if vampiricTouch - GetTime() &lt; 10 then CastSpellByName(GetSpellInfo(PQ_VT),bossUnit) return true end else if vampiricTouch - GetTime() &lt; GCD or vampiricTouch - GetTime() &lt; GCD + tick then CastSpellByName(GetSpellInfo(PQ_VT),bossUnit) return true end end end end end else CastSpellByName(GetSpellInfo(PQ_VT),bossUnit) return true end end end

local PQ_SWP = PQ_SWP local mentallyPower = mentallyPower local TargetValidation = TargetValidation local isMindControledUnit = isMindControledUnit local tick = PQ_Round(3/(1+(UnitSpellHaste(&quot;player&quot;)/100))) local max_timer = PQ_Round(PQ_PowerRound(18/(3/(1+(UnitSpellHaste(&quot;player&quot;)/100))))*(3/(1+(UnitSpellHaste(&quot;player&quot;)/100))),2)
if not PQI_MentallyOffensiveSettings_AutoBossDotting_enable then return false end if not PQR_SpellAvailable(PQ_SWP) then return false end
for i=1,4 do local bossUnit = &quot;boss&quot;..i local shadowWordPain = select(7,UnitDebuffID(bossUnit,PQ_SWP,&quot;PLAYER&quot;))
-- Let&apos;s try and detect immunities if UnitBuffID(bossUnit,116994) or UnitBuffID(bossUnit,122540) or UnitBuffID(bossUnit,123250) or UnitBuffID(bossUnit,106062) or UnitBuffID(bossUnit,110945) then return false end
if TargetValidation(bossUnit,PQ_SWP) then local id = UnitGUID(bossUnit)
if shadowWordPain then for i=1,#dotTracker do if dotTracker[i].guid == id and dotTracker[i].spellID == PQ_SWP then if UnitBuffID(&quot;player&quot;,138963) then if dotTracker[i].crit == true then if shadowWordPain - GetTime() &lt; max_timer/2 then CastSpellByName(GetSpellInfo(PQ_SWP),bossUnit) return true end end if dotTracker[i].crit == false then if shadowWordPain - GetTime() &lt; max_timer-1 then CastSpellByName(GetSpellInfo(PQ_SWP),bossUnit) return true end end else if mentallyPower(589) &gt; dotTracker[i].swpPower then if shadowWordPain - GetTime() &lt; max_timer/2 then CastSpellByName(GetSpellInfo(PQ_SWP),bossUnit) return true end else if shadowWordPain - GetTime() &lt; tick then CastSpellByName(GetSpellInfo(PQ_SWP),bossUnit) return true end end end end end else CastSpellByName(GetSpellInfo(PQ_SWP),bossUnit) return true end end end
